import React, { useState, useEffect } from 'react';
import '../index.css';
import Navigation from './Navigation';

const ImageSlideshow = () => {
  // Array of image URLs
  const images = [
    'https://ibb.co/Fzc9X2n',
    'https://ibb.co/fCb8qr7',
    'https://ibb.co/SdrjdVs',
    'https://ibb.co/qDHYBJ7',
    'https://ibb.co/q5QTKNZ',
    'https://ibb.co/kK6nXXV',
    'https://ibb.co/qMvtmr1',
    'https://ibb.co/NVXR6kr',
    'https://ibb.co/d7GQDzW',
    'https://ibb.co/FKR6c0W',
    'https://ibb.co/VST88KX',
    'https://ibb.co/SKvmfw6',
    'https://ibb.co/HCptHLb',
    'https://ibb.co/3m6Wzrb',
    'https://ibb.co/RyXqQdt',
    'https://ibb.co/k5vWnqw',
    'https://ibb.co/p1yHZcH',
    'https://ibb.co/25MVH9Z',
    'https://ibb.co/3cc3fR3',
    'https://ibb.co/5TjpZLF',
    'https://ibb.co/pxTB0cX',
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Function to handle the automatic slide change
    const slideInterval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000); // Change image every 3 seconds (3000 milliseconds)

    // Cleanup the interval on component unmount
    return () => clearInterval(slideInterval);
  }, [currentIndex, images.length]);

  return (

    <div className="slideshow-container">
      {images.map((image, index) => (
        <div
          key={index}
          className={index === currentIndex ? 'slide active' : 'slide'}
        >
          <img src={image} alt={`Slide ${index}`} className="image-slide" />
        </div>
      ))}
    </div>
  );
};

export default ImageSlideshow;
